import React, { useState, useEffect } from "react";
import "./Post.css";
import Avatar from "@material-ui/core/Avatar";
import { db } from "../../firebase";

function Post({ postId, username, caption, imageUrl }) {
  const [comments, setComments] = useState([]);
  const [comment, setComment] = useState("");

  // useEffect--> run a piece of code based on condtion and run every time whe condition change
  useEffect(() => {
    let unsubscribe;
    if (postId) {
      unsubscribe = db
        .collection("posts")
        .doc(postId)
        .collection("comments")
        .onSnapshot((snapshot) => {
          setComments(snapshot.docs.map((doc) => doc.data()));
        });
    }
    return () => {
      unsubscribe();
    };
  }, [postId]);

  const postComment = () => {};

  return (
    <div className="post">
      <div className="post-header">
        <Avatar className="post-avatar" src={imageUrl} alt={username} />
        <h3>{username}</h3>
      </div>
      <img className="post-image" src={imageUrl} alt="avtar" />

      <h4 className="post-text">
        <strong> {username} </strong>
        {caption}
      </h4>

      <div className="post-comments">
        {comments.map((comment) => (
          <p>
            <strong>{comment.username}</strong>
            {comment.text}
          </p>
        ))}
      </div>

      <form className="post-comment-box">
        <input
          className="post-input"
          type="text"
          placeholder="add a comment..."
          value={Comment}
          onChange={(e) => setComment(e.target.value)}
        />
        <button
          className="post-button"
          disabled={!comment}
          type="submit"
          onClick={postComment}
        >
          Post
        </button>
      </form>
    </div>
  );
}

export default Post;
