import React, { useState, useEffect } from "react";
import "./App.css";
import Post from "./component/Post";
import { db } from './firebase';



function App() {
  const [posts, setPosts] = useState([
    {
      username:"manpal",
      caption:"wow it is working",
      imageUrl:avatar1
    },
    {
      username:"Subhosree",
      caption:"subho it is working",
      imageUrl:avatar2
    },
    {
      username:"Manish",
      caption:"manisha it is working",
      imageUrl:avatar3
    },
    {
      username:"Shubham",
      caption:"wow shubham it is working",
      imageUrl:avatar4
    }
  ])

  // useEffect--> run a piece of code based on condtion and run every time whe condition change
  useEffect( () => {
    db.collection('posts').onSnapshot(snapshot => {
      setPosts(snapshot.docs.map( (doc) => doc))
    })
  }, [])

  return (
    <div className="app">
      <div className="app_header">
        <img
          className="app_headerImage"
          src="https://www.instagram.com/static/images/web/mobile_nav_type_logo.png/735145cfe0a4.png"
          alt="app_logo"
        />
      </div>

      {posts.map( (post) => (
        <Post username = {post.username} 
              caption = {post.caption}
              imageUrl = {post.imageUrl}      
        />
      ))
    }

    </div>
  );
}

export default App;
